def movePoint():
    global pointB
    global pointA
    global path
    if is_mouse_button_down(MOUSE_BUTTON_LEFT):
        if int(get_mouse_y()/10) < 41 and int(get_mouse_x()/10) < 81 :
            if areaMap[int(get_mouse_y()/10)][int(get_mouse_x()/10)] == 0:
                pointB = (int(get_mouse_y()/10),int(get_mouse_x()/10))
                path = AStar(areaMap).search(pointA,pointB)
    if is_mouse_button_down(MOUSE_BUTTON_RIGHT):
        if int(get_mouse_y()/10) < 41 and int(get_mouse_x()/10) < 81 :
            if areaMap[int(get_mouse_y()/10)][int(get_mouse_x()/10)] == 0:
                pointA = (int(get_mouse_y()/10),int(get_mouse_x()/10))
                path = AStar(areaMap).search(pointA,pointB)


def instruction():
    draw_text(f"Press T to change the view", 10, 10, 20, WHITE)
    draw_text(f"Press R to randomize the maze", 10, 35, 20, WHITE)
    draw_text(f"Press right mouse button to control blue", 10, 70, 20, WHITE)
    draw_text(f"Press left mouse button to control green", 10, 95, 20, WHITE)

    

while not window_should_close():
    begin_drawing()
    clear_background(backgroundColor)
    showTile(areaMap)
    if is_key_pressed(KEY_T):
        ran += 1
        areaMap = paths[ran%2]
        setPositionA(areaMap)
        setPositionB(areaMap)
        path = AStar(areaMap).search(pointA,pointB)
    if is_key_pressed(KEY_R):
        paths[0] = decodeMaze(createMaze())
        areaMap = paths[0]
        setPositionA(areaMap)
        setPositionB(areaMap)
        path = AStar(areaMap).search(pointA,pointB)
    
    drawPath(path)
    drawPoint()
    movePoint()
    instruction()
    end_drawing()
close_window()